import{o as n,a as s,i as a}from"./app.40ed1101.js";const t='{"title":"Usage","description":"","frontmatter":{},"headers":[{"level":2,"title":"In The Browser","slug":"in-the-browser"},{"level":2,"title":"In Single File Component","slug":"in-single-file-component"}],"relativePath":"guide/usage.md","lastUpdated":1622803424287}',p={},e=a('<h1 id="usage"><a class="header-anchor" href="#usage" aria-hidden="true">#</a> Usage</h1><h2 id="in-the-browser"><a class="header-anchor" href="#in-the-browser" aria-hidden="true">#</a> In The Browser</h2><p>Register the component in your javascript:</p><div class="language-js"><pre><code>Vue<span class="token punctuation">.</span><span class="token function">component</span><span class="token punctuation">(</span><span class="token string">&#39;QuillEditor&#39;</span><span class="token punctuation">,</span> VueQuill<span class="token punctuation">.</span>QuillEditor<span class="token punctuation">)</span><span class="token punctuation">;</span>\n</code></pre></div><p>Basic Usage:</p><div class="language-html"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>div</span> <span class="token attr-name">id</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>app<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>quill-editor</span> <span class="token attr-name">theme</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>snow<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>quill-editor</span><span class="token punctuation">&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>div</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><div class="tip custom-block"><p class="custom-block-title">INFO</p><p>We&#39;re showing you a simple example here, but in a typical Vue application, we use Single File Components instead of a string template. You can find <strong>SFC implementation</strong> in <a href="./usage.html#in-single-file-component">this section</a>.</p></div><h2 id="in-single-file-component"><a class="header-anchor" href="#in-single-file-component" aria-hidden="true">#</a> In Single File Component</h2><p><strong>Global Registration:</strong></p><div class="language-javascript"><pre><code><span class="token keyword">import</span> <span class="token punctuation">{</span> createApp <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;vue&#39;</span>\n<span class="token keyword">import</span> <span class="token punctuation">{</span> QuillEditor <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;@vueup/vue-quill&#39;</span>\n<span class="token keyword">import</span> <span class="token string">&#39;@vueup/vue-quill/dist/vue-quill.snow.css&#39;</span><span class="token punctuation">;</span>\n\n<span class="token keyword">const</span> app <span class="token operator">=</span> <span class="token function">createApp</span><span class="token punctuation">(</span><span class="token punctuation">)</span>\napp<span class="token punctuation">.</span><span class="token function">component</span><span class="token punctuation">(</span><span class="token string">&#39;QuillEditor&#39;</span><span class="token punctuation">,</span> QuillEditor<span class="token punctuation">)</span>\n</code></pre></div><p><strong>or Local Registration:</strong></p><div class="language-javascript"><pre><code><span class="token keyword">import</span> <span class="token punctuation">{</span> QuillEditor <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;@vueup/vue-quill&#39;</span>\n<span class="token keyword">import</span> <span class="token string">&#39;@vueup/vue-quill/dist/vue-quill.snow.css&#39;</span><span class="token punctuation">;</span>\n\n<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token punctuation">{</span>\n  components<span class="token operator">:</span> <span class="token punctuation">{</span>\n    QuillEditor\n  <span class="token punctuation">}</span>\n<span class="token punctuation">}</span>\n</code></pre></div><p><strong>Basic Usage:</strong></p><div class="language-vue"><pre><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>template</span><span class="token punctuation">&gt;</span></span>\n  <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>QuillEditor</span> <span class="token attr-name">theme</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>snow<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>\n<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>template</span><span class="token punctuation">&gt;</span></span>\n</code></pre></div><div class="tip custom-block"><p class="custom-block-title">NOTE</p><p>The component itself does not include any CSS theme. You&#39;ll need to include it separately: <code>import &#39;@vueup/vue-quill/dist/vue-quill.snow.css&#39;</code> or <code>import &#39;@vueup/vue-quill/dist/vue-quill.bubble.css&#39;</code></p></div>',15);p.render=function(a,t,p,o,l,c){return n(),s("div",null,[e])};export default p;export{t as __pageData};
